import React, { useState } from "react";
import axios from "axios";
import { Form } from "react-bootstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default function Register() {
  const [user, setUser] = useState({});

  const getValue = (e) => {
    setUser(() => {
      return { ...user, [e.target.name]: e.target.value };
    });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    axios({
      method: "POST",
      url: "/users/add",
      data: user,
    })
      .then(function (response) {
        alert(`${response.data.email} is already registered`)
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return (
    <main>
      <h1>Register</h1>
      <Form
        onSubmit={(e) => {
          submitHandler(e);
        }}
      >
        <Form.Group>
          <label HTMLFor="registerEmail">Email address</label>
          <input
            type="email"
            className="form-control"
            id="registerEmail"
            placeholder="Enter email"
            name="email"
            onChange={getValue}
          />
        </Form.Group>
        <Form.Group>
          <label HTMLFor="registerPassword">Password</label>
          <input
            type="password"
            className="form-control"
            id="registerPassword"
            placeholder="Password"
            name="password"
            onChange={getValue}
          />
        </Form.Group>
        <Form.Group>
          <label HTMLFor="registerName">Name</label>
          <input
            type="text"
            className="form-control"
            id="registerName"
            placeholder="Name"
            name="name"
            onChange={getValue}
          />
        </Form.Group>
        <Form.Group>
          <label HTMLFor="registerSpiritAnimal">Spirit Animal</label>
          <input
            type="text"
            className="form-control"
            id="registerSpiritAnimal"
            placeholder="Spirit Animal"
            name="spiritAnimal"
            onChange={getValue}
          />
        </Form.Group>
        <input type="submit" value="submit" className="btn btn-primary" />
      </Form>
      <p>
        already registered? please <Link to="./login">log in</Link>
      </p>
    </main>
  );
}
