import React , {useState} from "react";
import axios from "axios";
import {
  Button,
  Form,
} from "react-bootstrap";

export default function Register() {

const [user, setUser] = useState ({});

const getValue = (e) => {

  setUser(()=>{
    return {...user, [e.target.name]:e.target.value}
  })
}

  const submitHandler = (e) => {
e.preventDefault()
  axios({
    method: "POST",
    url: "http//localhost:3500/users/add",
    data: user
  }).then(function (response) {
    console.log(response);
  })
  .catch(function (error) {
    console.log(error);
  });

}



  return (
    <main>
      <h1>Register</h1>
      <Form onSubmit = {submitHandler}>
        <Form.Group>
          <label forHTML="registerEmail">Email address</label>
          <input
            type="email"
            className="form-control"
            id="registerEmail"
            placeholder="Enter email"
            name="email"
            onChange={getValue}
          />
        </Form.Group>
        <Form.Group>
          <label forHTML="registerPassword">Password</label>
          <input
            type="password"
            className="form-control"
            id="registerPassword"
            placeholder="Password"
            name="password"
            onChange={getValue}
          />
        </Form.Group>
        <Form.Group>
          <label forHTML="registerName">Name</label>
          <input
            type="text"
            className="form-control"
            id="registerName"
            placeholder="Name"
            name="name"
            onChange={getValue}
          />
        </Form.Group>
        <Form.Group>
          <label forHTML="registerSpiritAnimal">Spirit Animal</label>
          <input
            type="text"
            className="form-control"
            id="registeSpiritAnimal"
            placeholder="Spirit Animal"
            name="spiritAnimal"
            onChange={getValue}
          />
        </Form.Group>
        <Button type="submit" className="btn btn-primary">
          Submit
        </Button>
      </Form>
    </main>
  );
}
