import React, { useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default function Login() {
  const [user, setUser] = useState({});

  const getValue = (e) => {
    setUser(() => {
      return { ...user, [e.target.name]: e.target.value };
    });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    axios({
      method: "POST",
      url: "/users/login",
      data: user,
    })
      .then((response) => {
        response.data.login === true ? alert("nice") : alert("not nice");
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <main>
      <h1>Login</h1>
      <form onSubmit={(e)=>{}}>
        <div className="form-group">
          <label forHTML="loginEmail">Email address</label>
          <input
            type="email"
            className="form-control"
            id="registerEmail"
            placeholder="Enter email"
            name="email"
            onChange={getValue}
          />
        </div>
        <div className="form-group">
          <label forHTML="loginPassword">Password</label>
          <input
            type="password"
            className="form-control"
            id="loginPassword"
            placeholder="Password"
            name="password"
            onChange={getValue}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
      <p>
        not a member yet? please <Link to="/register">register</Link>
      </p>
    </main>
  );
}
