import React, { useState } from "react";
import axios from "axios";

export default function Update() {

  const [user, setUser] = useState({});

  const getValue = (e) => {
    setUser(() => {
      return { ...user, [e.target.name]: e.target.value };
    });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    axios({
      method: "PUT",
      url: "/users/update",
      data: user
    })
      .then((response) => {
        console.log(response);
        response.data.updated === true ? alert("nice") : alert("not nice");
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <main>
      <h1>Profile page</h1>
      <form onSubmit={(e)=>{submitHandler(e)}}>
        <div className="form-group">
          <label htmlFor="updateEmail">Email address</label>
          <input
            type="email"
            className="form-control"
            id="updateEmail"
            placeholder="Enter email"
            name="email"
            onChange={getValue}
          />
        </div>
        <div className="form-group">
          <label htmlFor="updatePassword">Password</label>
          <input
            type="password"
            className="form-control"
            id="updatePassword"
            placeholder="Password"
            name="password"
            onChange={getValue}
          />
        </div>
        <div className="form-group">
          <label htmlFor="updateNeWPassword">New Password</label>
          <input
            type="Password"
            className="form-control"
            id="updateNewPassword"
            placeholder="New Password"
            name="newPassword"
            onChange={getValue}
          />
        </div>
        <div className="form-group">
          <label htmlFor="updateName">Name</label>
          <input
            type="text"
            className="form-control"
            id="updateName"
            placeholder="Name"
            name="name"
            onChange={getValue}
          />
        </div>
        <div className="form-group">
          <label htmlFor="updateSpiritAnimal">Spirit Animal</label>
          <input
            type="text"
            className="form-control"
            id="updateSpiritAnimal"
            placeholder="Spirit Animal"
            name="spiritAnimal"
            onChange={getValue}
          />
        </div>
        <input type="submit" value="submit" className="btn btn-primary"/>
      </form>
    </main>
  );
}
